CREATE TABLE AGENDA (ID_AGE INTEGER NOT NULL, RESOURCEID INTEGER, PARENTID INTEGER, CAPTION VARCHAR(255), START_AGE DATE, FINISH DATE, STATE INTEGER, ACTUALFINISH INTEGER, ACTUALSTART INTEGER, EVENTTYPE INTEGER, LABELCOLOR INTEGER, LOCATION VARCHAR(255), MESSAGE VARCHAR(255), OPTIONS INTEGER, RECURRENCEINDEX INTEGER, RECURRENCEINFO BLOB SUB_TYPE 0 SEGMENT SIZE 80, REMINDERDATE DATE, REMINDERMINUTESBEFORESTART  INTEGER, REMINDERRESOURCESDATA BLOB SUB_TYPE 0 SEGMENT SIZE 80, TASKCOMPLETEFIELD INTEGER, TASKINDEXFIELD INTEGER, TASKLINKSFIELD BLOB SUB_TYPE 0 SEGMENT SIZE 80, TASKSTATUSFIELD INTEGER, CLIENTE VARCHAR (6));
ALTER TABLE AGENDA ADD CONSTRAINT PK_AGENDA PRIMARY KEY (ID_AGE);
CREATE SEQUENCE GEN_AGENDA_ID;
ALTER SEQUENCE GEN_AGENDA_ID RESTART WITH 0;
create trigger agenda_bi for agenda active before insert position 0 as begin if (new.resourceid is null) then new.resourceid = gen_id(gen_agenda_id,1); end